import { useState } from "react";
import "./App.css";

const App = () => {
  const [count, setCount] = useState(0);
  const [step, setStep] = useState(1);
  let currentDate = new Date();

  function updateDate() {
    console.log(count);
    currentDate.setDate(currentDate.getDate() + count);
    let updatedDate = currentDate.toDateString();
    return updatedDate;
  }

  return (
    <div className="container">
      <div className="steps">
        <button className="prev_step" onClick={() => setStep(step - 1)}>
          -
        </button>
        <p>Step: {step}</p>
        <button className="next_step" onClick={() => setStep(step + 1)}>
          +
        </button>
      </div>
      <div className="steps">
        <button className="prev_count" onClick={() => setCount(count - step)}>
          -
        </button>
        <p>Count: {count}</p>
        <button className="next_count" onClick={() => setCount(count + step)}>
          +
        </button>
      </div>
      <p>{count > 0 ? `${count} days from today is ${updateDate()}` : ``}</p>
    </div>
  );
};

export default App;
