import { useState } from "react";
import "./App.css";

const App = () => {
  const [count, setCount] = useState(0);
  let currentDate = new Date();

  function updateDate(count) {
    currentDate.setDate(currentDate.getDate() + count);
    let updatedDate = currentDate.toDateString();
    console.log(updatedDate);
    return updatedDate;
  }

  return (
    <div className="container">
      <div className="steps">
        <button className="prev_step">-</button>
        <p>Step: 1</p>
        <button className="next_step">+</button>
      </div>
      <div className="steps">
        <button className="prev_count">-</button>
        <p>Count: 0</p>
        <button className="next_count">+</button>
      </div>
      <p>Today is {updateDate(0)}</p>
    </div>
  );
};

export default App;
