import { useState } from "react";
import "./App.css";

const App = () => {
  const [count, setCount] = useState(0);
  const [step, setStep] = useState(1);
  let currentDate = new Date();

  function updateDate() {
    currentDate.setDate(currentDate.getDate() + count);
    let updatedDate = currentDate.toDateString();
    return updatedDate;
  }

  return (
    <div className="container">
      <div className="steps">
        <input type="range" min="1" max="7" />
        {/* <button className="prev_step" onClick={() => setStep(step - 1)}>
          -
        </button> */}
        {/* <p>Step: {step}</p>
        <button className="next_step" onClick={() => setStep(step + 1)}>
          +
        </button> */}
      </div>
      <div className="steps">
        <button className="prev_count" onClick={() => setCount(count - step)}>
          -
        </button>
        <input type="number" />
        <button className="next_count" onClick={() => setCount(count + step)}>
          +
        </button>
      </div>
      <p>
        {count === 0
          ? `Today is ${updateDate()}`
          : count > 0
          ? `${count} days from today is ${updateDate()}`
          : `${count * -1} days ago was ${updateDate()}`}
      </p>
      <button>Reset</button>
    </div>
  );
};

export default App;
